SIP-Authorization ::= < AVP Header: 380 >
                      { Digest-Username }
                      { Digest-Realm }
                      { Digest-Nonce }
                      { Digest-URI }
                      { Digest-Response }
                      [ Digest-Algorithm ]
                      [ Digest-CNonce ]
                      [ Digest-Opaque ]
                      [ Digest-QoP ]
                      [ Digest-Nonce-Count ]
                      [ Digest-Method]
                      [ Digest-Entity-Body-Hash ]
                    * [ Digest-Auth-Param ]
                    * [ AVP ]

SIP-User-Data ::= < AVP Header: 389 >
                  { SIP-User-Data-Type }
                  { SIP-User-Data-Contents }
                * [ AVP ]
