module ex-ethernet {
  namespace "http://example.com/ethernet";
  prefix "eth";

import ietf-interfaces {
  prefix if;
}
import iana-if-type {
  prefix ianaift;
}

// configuration parameters for Ethernet interfaces
augment "/if:interfaces/if:interface" {
  when "if:type = 'ianaift:ethernetCsmacd'";

  container ethernet {
    choice transmission-params {
      case auto {
        leaf auto-negotiate {
          type empty;
        }
      }
      case manual {
        leaf duplex {
          type enumeration {
            enum "half";
            enum "full";
          }
        }
        leaf speed {
          type enumeration {
            enum "10Mb";
            enum "100Mb";
            enum "1Gb";
            enum "10Gb";
          }
        }
      }
    }
    // other Ethernet-specific params...
  }
}

// operational state parameters for Ethernet interfaces
augment "/if:interfaces-state/if:interface" {
  when "if:type = 'ianaift:ethernetCsmacd'";

    container ethernet {
      leaf duplex {
        type enumeration {
          enum "half";
          enum "full";
        }
      }
      // other Ethernet-specific params...
    }
  }
}

module ex-ethernet-bonding {
  namespace "http://example.com/ethernet-bonding";
  prefix "bond";

import ietf-interfaces {
  prefix if;
}
import iana-if-type {
  prefix ianaift;
}
augment "/if:interfaces/if:interface" {
  when "if:type = 'ianaift:ieee8023adLag'";

    leaf-list slave-if {
      type if:interface-ref;
      must "/if:interfaces/if:interface[if:name = current()]"
         + "/if:type = 'ianaift:ethernetCsmacd'" {
        description
          "The type of a slave interface must be 'ethernetCsmacd'.";
      }
    }
    leaf bonding-mode {
      type enumeration {
        enum round-robin;
        enum active-backup;
        enum broadcast;
      }
    }
    // other bonding config params, failover times, etc.
  }
}

module ex-vlan {
  namespace "http://example.com/vlan";
  prefix "vlan";

import ietf-interfaces {
  prefix if;
}
import iana-if-type {
  prefix ianaift;
}

augment "/if:interfaces/if:interface" {
  when "if:type = 'ianaift:ethernetCsmacd' or
        if:type = 'ianaift:ieee8023adLag'";
  leaf vlan-tagging {
    type boolean;
    default false;
  }
}
augment "/if:interfaces/if:interface" {
  when "if:type = 'ianaift:l2vlan'";

    leaf base-interface {
      type if:interface-ref;
      must "/if:interfaces/if:interface[if:name = current()]"
         + "/vlan:vlan-tagging = 'true'" {
        description
          "The base interface must have VLAN tagging enabled.";
      }
    }
    leaf vlan-id {
      type uint16 {
        range "1..4094";
      }
      must "../base-interface" {
        description
          "If a vlan-id is defined, a base-interface must
           be specified.";
      }
    }
  }
}
