"info": {
  "primary-source": "Olson:2011m",
  "formats": [
    "text/calendar",
    "application/calendar+xml",
    "application/calendar+json"
  ],
  "truncated" : {
    "any": false,
    "ranges": [
      {
        "start": "1970-01-01T00:00:00Z",
        "end": "*"
      },
      {
        "start":"2010-01-01T00:00:00Z",
        "end":"2020-01-01T00:00:00Z"
      }
    ],
    "untruncated": true
  },
  "provider-details": "http://tz.example.com/about.html",
  "contacts": ["mailto:tzs@example.org"]
},

"actions": [
  {
    "name": "capabilities",
    "uri-template": "/servlet/timezone/capabilities",
    "parameters": []
  },

{
  "name": "list",
  "uri-template": "/servlet/timezone/zones{?changedsince}",
  "parameters": [
    {
      "name": "changedsince",
      "required": false,
      "multi": false
    }
  ]
},

{
  "name": "get",
  "uri-template": "/servlet/timezone/zones{/tzid}{?start,end}",
  "parameters": [
    {
      "name": "start",
      "required": false,
      "multi": false
    },
    {
      "name": "end",
      "required": false,
      "multi": false
    }
  ]
},

{
  "name": "expand",
  "uri-template":
    "/servlet/timezone/zones{/tzid}/observances{?start,end}",
  "parameters": [
    {
      "name": "start",
      "required": true,
      "multi": false
    },
    {
      "name": "end",

      "required": true,
      "multi": false
    }
  ]
},

{
  "name": "find",
  "uri-template": "/servlet/timezone/zones{?pattern}",
  "parameters": [
    {
      "name": "pattern",
      "required": true,
      "multi": false
    }
  ]
},

    {
      "name": "leapseconds",
      "uri-template": "/servlet/timezone/leapseconds",
      "parameters": []
    }
  ]
}

