{
    "$schema": "https://json-schema.org/draft-04/schema#",
    "definitions": {
        "adapt_method": {
            "enum": [
                "UDP_without_DTLS",
                "UDP_with_DTLS",
                "IPsec",
                "Client_NAT"
            ],
            "type": "string"
        },
        "conv_method": {
            "enum": [
                "GMA",
                "MPTCP_Proxy",
                "GRE_Aggregation_Proxy",
                "MPQUIC"
            ],
            "type": "string"
        },
        "supported": {
            "type": "boolean"
        },
        "active": {
            "type": "boolean"
        },
        "connection_id": {
            "type": "integer"
        },
        "feature_name": {
            "enum": [
                "lossless_switching",
                "fragmentation",
                "concatenation",
                "uplink_aggregation",
                "downlink_aggregation",
                "measurement"
                "probing"
            ],
            "type": "string"
        },
        "connection_type": {
            "enum": [
                "Wi-Fi",
                "5G_NR",
                "MulteFire",
                "LTE"
            ],
            "type": "string"
        },
        "ip_address": {
                "type": "string"
        },
        "port": {
            "maximum": 65535,
            "minimum": 1,
            "type": "integer"
        },
        "adaptation_method": {
            "allOf" : [
                { "$ref": "#/definitions/adapt_method" },
                { "$ref": "#/definitions/supported" }
            ]
        },
        "connection": {
            "allOf" : [
                { "$ref": "#/definitions/connection_id" },
                { "$ref": "#/definitions/connection_type" }
            ]
        },
        "convergence_method": {
            "allOf": [
                { "$ref": "#/definitions/conv_method" },
                { "$ref": "#/definitions/supported" }
            ]
        },
        "feature_status": {
            "allOf": [
                { "$ref": "#/definitions/feature_name" },
                { "$ref": "#/definitions/active" }
            ]
        },
        "ncm_end_point": {
            "allOf" : [
                { "$ref" : "#/definitions/ip_address" },
                { "$ref" : "#/definitions/port" }
            ]
        },
        "capability_acknowledgment" : {
            "enum" : [
                "MX_ACCEPT",
                "MX_REJECT"
            ],
            "type" : "string"
        },
        "threshold" : {
            "high" : {
                "type" : "integer"
            },
            "low" : {
                "type" : "integer"
            },
            "type" : "object"
        },
        "meas_report_param" : {
            "enum" : [
                    "WLAN_RSSI",
                    "WLAN_LOAD",
                    "LTE_RSRP",
                    "LTE_RSRQ",
                    "UL_TPUT",
                    "DL_TPUT",
                    "EST_UL_TPUT",
                    "EST_DL_TPUT",
                    "NR_RSRP",
                    "NR_RSRQ"
            ],
            "type" : "string"
        },
        "meas_report_conf" : {
            "meas_rep_param" : {
                "$ref" : "#definitions/meas_report_param"
            },
            "meas_threshold" : {
                "$ref" : "#definitions/threshold"
            },
            "meas_period_ms" : {
                "type" : "integer"
            },
            "type" : "object"
        },
        "ssid_types" : {
            "enum" : [
                "ssid",
                "bssid",
                "hessid"
            ],
            "type" : "string"
        },
        "ip_addr_mask" : {
            "type" : "string",
            "default" : "0.0.0.0/0"
        },
        "port_range" : {
            "start" : {
                "type" : "integer",
                "default" : 0
            },
            "end" : {
                "type" : "integer",
                "default" : 65535
            }
        },
        "traffic_flow_template" : {
            "remote_addr_mask" : {
                "$ref" : "#definitions/ip_addr_mask" },
            "local_addr_mask" : {
                "$ref" : "#definitions/ip_addr_mask" },
            "protocol_type" : {
                "type" : "integer",
                "minimum" : 0,
                "maximum" : 255
            },
            "local_port_range" : {
                "$ref" : "#definitions/port_range" },
            "remote_port_range" : {
                "$ref" : "#definitions/port_range" },
            "traffic_class" : {
                "type" : "integer",
                "default" : 255
            },
            "flow_label" : {
                "type" : "integer",
                "default" : 0
            }
        },
        "delivery_node_id" : {
            "type" : "string"
        },
        "unique_session_id" : {
            "type" : "object",
            "ncm_id" : {
              "type" : "integer"
            },
            "session_id" : {
              "type" : "integer"
            }
        },
        "keep_alive_reason" : {
            "enum" : [
                "Timeout",
                "Handover"
            ],
            "type" : "string"
        },
        "connection_status" : {
            "enum" : [
                "disabled",
                "enabled",
                "connected"
            ],
            "type" : "string",
            "default" : "connected"
        },
        "adaptation_param" : {
           "udp_adapt_port" : {
              "type" : "integer"
           }
        },
        "probe_param" : {
            "probe_port" : {
                "type" : "integer"
            },
            "anchor_conn_id" : {
                "type" : "integer"
            },
            "mx_configuration_id" : {
                "type" : "integer"
            }
    },
        "client_param" : {
            "connection_id" : {
                "type" : "integer"
            },
            "adapt_param" : {
                "type" : {"$ref" : "#definitions/adaptation_param" }
            }
        }
    },
            "adapt_param": {
            "tunnel_ip_addr": {
                "type": "string"
            },
            "tunnel_end_port": {
                "type": "integer"
            },
            "shared_secret": {
                "type": "string"
            },
            "mx_header_optimization": {
                "type": "boolean",
                "default": false
            }
        },
        "delivery_connection": {
            "connection_id": {
                "$ref": "#definitions/connection_id"
            },
            "connection_type": {
                "$ref": "#definitions/connection_type"
            },
            "adaptation_method": {
                "$ref": "#definitions/adapt_method"
            },
            "adaptation_method_param": {
                "$ref": "#definitions/adapt_param"
            }
        },
        "app_madp_assoc": {
            "anchor_conn_id" : {
                "type" : "integer"
            },
            "mx_configuration_id" : {
                "type" : "integer"
            }

            "ul_tft_list": {
                "items": {
                    "$ref": "#definitions/traffic_flow_template"
                },
                "type": "array"
            },
            "dl_tft_list": {
                "items": {
                    "$ref": "#definitions/traffic_flow_template"
                },
                "type": "array"
            }
        },
        "predict_param_name": {
            "enum": [
                "validity_time",
                "bandwidth",
                "jitter",
                "latency",
                "signal_quality"
            ],
            "type": "string"
        },
        "predict_add_param_name": {
            "enum": [
                "WLAN_RSSI",
                "WLAN_LOAD",
                "LTE_RSRP",
                "LTE_RSRQ",
                "NR_RSRP",
                "NR_RSRQ"
            ],
            "type": "string"
        },
    "id": "https://example.com/mams/definitions.json"
}

