/* Managed by IANA */
 enum {
     X509(0),
     RawPublicKey(2),
     1609Dot2(3),
     (255)
 } CertificateType;

/* certificate type defined in this document.*/
 case 1609Dot2:
 opaque cert_data<1..2^24-1>;

 /* RawPublicKey defined in RFC 7250*/
case RawPublicKey:
opaque ASN.1_subjectPublicKeyInfo<1..2^24-1>;

/* X.509 certificate defined in RFC 8446*/
case X.509:
opaque cert_data<1..2^24-1>;

ClientHello,
client_certificate_type=1609Dot2,
server_certificate_type=1609Dot2,  -------->    ServerHello,
                                       {EncryptedExtensions}
                          {client_certificate_type=1609Dot2}
                          {server_certificate_type=1609Dot2}
                                        {CertificateRequest}
                                               {Certificate}
                                         {CertificateVerify}
                                                  {Finished}
  {Certificate}          <-------         [Application Data]
  {CertificateVerify}
  {Finished}             -------->
  [Application Data]     <------->        [Application Data]

Client                                           Server
ClientHello,
client_certificate_type=(1609Dot2),
server_certificate_type=(1609Dot2,
X509,RawPublicKey),         ----------->         ServerHello,
                                        {EncryptedExtensions}
                           {client_certificate_type=1609Dot2}
                               {server_certificate_type=X509}
                                         {CertificateRequest}
                                                {Certificate}
                                          {CertificateVerify}
                                                   {Finished}
                            <---------     [Application Data]
{Finished}                  --------->
[Application Data]          <-------->     [Application Data]
